import {useEffect, useState} from "react";
import ReactSlider from "react-slider";
import axios from "axios";

function Cocktails() {
    const[searchTerm, changeSearch] = useState("");
    const[cocktailList, setCocktailList] = useState([]);
    const[listLength, setListLength] = useState(0);
    const[showDrink, setShowDrink] = useState(false);
    const[currentDrink, setCD] = useState(-1);

    const runSearch = () => {
        console.log("running search")
        if (searchTerm != "") {
            axios.get("https://www.thecocktaildb.com/api/json/v1/1/search.php?s="+searchTerm)

            .then((response) => {
                console.log(response.data.drinks)
                setCocktailList(response.data.drinks)
                setListLength(response.data.drinks.length)
            })
        } else {
            setCocktailList([])
            setListLength(0)
        }
    }

    const drinkListRendered = () => {
        // console.log("drink list rendered")
        if (cocktailList == null) {
            return null
        }

        if (cocktailList.length == 1) {
            const drinks = cocktailList.map((d) => (
                <div
                    style={{
                        width: "60%",
                        marginLeft: "20%",
                        marginRight: "20%",
                        marginBottom: "5%",
                        backgroundColor: "black",
                        float: 'left',    
                    }} 
                    onClick={() => {
                        console.log(cocktailList.indexOf(d))
                        setShowDrink(true)
                        setCD(cocktailList.indexOf(d))
                    }}
                >
                    <img style={{height: '100%', width: '100%', objectFit: "contain"}} src={d.strDrinkThumb}/>
                    {/* {cocktailList.indexOf(d)}.&nbsp;  */}
                    <div style={{width: "80%", marginLeft: "10%", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", lineHeight: "1.5em", height: "1.5em"}}>
                        {d.strDrink}
                    </div>

                </div>    
            ))

            return drinks
        } else if (cocktailList.length == 2) {
            const drinks = cocktailList.map((d) => (
                <div
                    style={{
                        width: "45%",
                        marginLeft: "2.5%",
                        marginRight: "2.5%",
                        marginBottom: "5%",
                        backgroundColor: "black",
                        float: 'left',    
                    }} 
                    onClick={() => {
                        console.log(cocktailList.indexOf(d))
                        setShowDrink(true)
                        setCD(cocktailList.indexOf(d))
                    }}
                >
                    <img style={{height: '100%', width: '100%', objectFit: "contain"}} src={d.strDrinkThumb}/>
                    {/* {cocktailList.indexOf(d)}.&nbsp;  */}
                    <div style={{width: "80%", marginLeft: "10%", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", lineHeight: "1.5em", height: "1.5em"}}>
                        {d.strDrink}
                    </div>

                </div>    
            ))

            return drinks
        } else {
            const drinks = cocktailList.map((d) => (
                <div
                    style={{
                        width: "28%",
                        marginLeft: "2.5%",
                        marginRight: "2.5%",
                        marginBottom: "5%",
                        backgroundColor: "black",
                        float: 'left',

                    }} 
                    onClick={() => {
                        console.log(cocktailList.indexOf(d))
                        setShowDrink(true)
                        setCD(cocktailList.indexOf(d))
                    }}
                >
                    <img style={{height: '100%', width: '100%', objectFit: "contain"}} src={d.strDrinkThumb}/>
                    {/* {cocktailList.indexOf(d)}.&nbsp;  */}
                    <div style={{width: "80%", marginLeft: "10%", whiteSpace: "nowrap", overflow: "hidden", textOverflow: "ellipsis", lineHeight: "1.5em", height: "1.5em"}}>
                        {d.strDrink}
                    </div>

                </div>    
            ))

            return drinks
        }

    }

    const ingredientsRendered = (cd) => {
        const ing = cd.map((i) => (
            <li>
                {i}
            </li>
        ))

        return ing
    }

    const instructionsRendered = (cd) => {
        const ins = cd.map((i) => (
            <li>
                {i}
            </li>
        ))

        return ins
    }

    const parseDrink = () => {
        var cd = cocktailList[currentDrink]

        var ingredientsList = []
        var measurementsList = []
        var stepList = []

        for (var name in cd) {
            if (name.startsWith("strIngredient") && cd[name] != null) {
                ingredientsList.push(cd[name])
            }

            if (name.startsWith("strMeasure") && cd[name] != null) {
                measurementsList.push(cd[name])
            }

            if (name == "strInstructions" && cd[name] != null) {
                stepList = cd[name].split('.')
            }
        }

        console.log(ingredientsList)
        console.log(measurementsList)
        console.log(stepList)

        var stepList2 = []

        stepList.forEach(element => {
            if (element != "") {
                stepList2.push(element)
            }
        });

        var combinedIngredients = []

        for (let i = 0; i < ingredientsList.length; i++) {
            if (measurementsList[i].endsWith(' ')) {
                combinedIngredients.push(measurementsList[i] + ingredientsList[i])
            } else {
                combinedIngredients.push(measurementsList[i] + " " + ingredientsList[i])
            }
        }

        console.log(combinedIngredients)

        var retDrink = {
            name: cd.strDrink,
            ingredients: combinedIngredients,
            steps: stepList2
        }

        console.log(retDrink)
        return retDrink
    }

    const drinkModal = () => {

        if (!showDrink) {
            return null
        } else {
            var cd = parseDrink()
            console.log(cocktailList[currentDrink])

            return (
                <div
                    style={{
                        height: "100%",
                        width: "100%",
                        zIndex: "101",
                        left: "0",
                        top: "0",
                        overflow: "auto",
                        position: "fixed",
                        display: "grid",
                        backgroundColor: "rgba(24, 25, 26, 0.8)",
                    }}      
                >
                    <div
                        style={{
                            position: "relative",
                            justifySelf: "center",
                            alignSelf: "center",
                            display: "block",
                            // border: "#ff6505 solid",
                            backgroundColor: "black",
                            height: "auto",
                            width: "80vw",
                            zIndex: "102",
                            // padding: "10px 0px 10px 0px",
                            // borderRadius: "20px",
                        }}

                        // onClick={() => {
                        //     parseDrink();
                        // }}
                    >

                        {/* {cocktailList[currentDrink].strDrink} */}
                        <div style={{width: "40%", height: "100%", float: "left"}}>


                            <img style={{paddingTop: "10%", width: '80%', objectFit: "contain"}} src={cocktailList[currentDrink].strDrinkThumb}/>

                            <div style={{
                                borderColor: "blue",
                                borderLeft: "2px solid",
                                margin: "5% 0% 10% 10%",

                            }}>
                                <div>
                                    {cocktailList[currentDrink].strDrink}
                                </div>

                                <div>
                                    Glassware: {cocktailList[currentDrink].strGlass}
                                </div>
                            </div>

                        </div>

                        <div style={{width: "60%", height: "100%", float: "left"}}>
                            <div
                                style= {{
                                    textAlign: "right",
                                    fontWeight: "bolder",
                                    height: "10%",
                                    fontSize: "30px"

                                }}
                                onClick={() => {
                                    setCD(-1)
                                    setShowDrink(false)
                                }}
                            >
                                &#215;
                            </div>

                            <div style={{textAlign: 'left', fontWeight: "bold"}}>Ingredients</div>

                            <div style={{
                                borderColor: "blue",
                                borderLeft: "2px solid"
                            }}>

                                <ul style={{textAlign: 'left', fontStyle: "italic"}}>
                                    {ingredientsRendered(cd.ingredients)}
                                </ul>
                            </div>

                            <div style={{textAlign: 'left', fontWeight: "bold"}}>Instructions:</div>

                            <div style={{
                                borderColor: "blue",
                                borderLeft: "2px solid"
                            }}>

                                <ol style={{textAlign: 'left', fontStyle: "italic"}}>
                                    {instructionsRendered(cd.steps)}
                                </ol>
                            </div>


                        </div>
                    </div>
                </div>
            )
        }
    }

    return(
        <div>
            {drinkModal()}
            <div className="MainContent">
                You're early
                <br/>
                <input 
                    type="text"
                    onChange={(e) => {
                        console.log(e.target.value)
                        changeSearch(e.target.value)
                    }}
                    onKeyPress={(e) => {
                        if(e.key === 'Enter'){
                            console.log('enter press here! ')
                            runSearch();
                          }
                    }}
                />
                <button
                    onClick={() => {
                        runSearch()
                    }}
                >
                    Test
                </button>
                <br/>
                <button
                    onClick={() => {
                        console.log(cocktailList)
                    }}
                >
                    Test List
                </button>
                <br/>
                <div>
                    {listLength} results
                </div>
                <br/>
                <div style={{
                    width: "80%",
                    marginLeft: "10%"
                }}>
                    {drinkListRendered()}
                </div>
            </div>
        </div>
    )

}

export default Cocktails
