import { useEffect, useState } from "react";
import "./dropdown_box.css"

function DropdownBox(props) {
    const[toggleVal, setToggle] = useState(false);

    var items = props.items;
    var columns = props.columns;
    var color = props.color;
    var title = props.title;
    // var items = ["Data Structures & Algorithms in Python", "Math Foundations for Decisions & Data Sciences", "AI and Data Engineering", "Data Mining"]
    // var columns = 2;

    var itemWidth = (1/columns)*100;
    console.log(itemWidth)


    return (

            <div onClick={() => {setToggle(!toggleVal)}} style={{width: "100%", border: "10px", float: "none", backgroundColor: "transparent"}}>
                <div style={{width: "100%"}}/>
                <div className="info" style={{backgroundColor: `${color}`, color: "white", float: "none", width: "100%"}}>
                    <div className={toggleVal ? ("triangledown"):("triangleright")}/>
                    {title}
                </div>
                {
                    (!toggleVal) ? (
                        // <div className="info" style={{backgroundColor: `${color}`, color: "white", width: "100%"}}>
                        //     <span className="triangleright"/>
                        //     {title}
                        // </div>
                        null
                    ) : (
                        <div>
                            <div style={{width: "100%"}}>
                                {
                                    items.map((item) => (
                                        <p id = "item"
                                            className="info"
                                            style={{
                                                width: `${itemWidth}%`,
                                                float: "left", 
                                                textAlign: "center",
                                            }}>
                                            {item}
                                        </p>
                                    ))
                                }
                                <div style={{width: "100%"}}/>
                            </div>      

                        </div>
                    )
                }
            </div>

    )

}

export default DropdownBox;
