import {useEffect, useState} from "react";
import ReactSlider from "react-slider";
import axios from "axios";

function Cocktails() {
    const[searchTerm, changeSearch] = useState("");
    const[cocktailList, setCocktailList] = useState([]);
    const[listLength, setListLength] = useState(0);
    const[showDrink, setShowDrink] = useState(false);
    const[currentDrink, setCD] = useState(-1);

    const runSearch = () => {
        console.log("running search")
        if (searchTerm != "") {
            axios.get("https://www.thecocktaildb.com/api/json/v1/1/search.php?s="+searchTerm)

            .then((response) => {
                console.log(response.data.drinks)
                setCocktailList(response.data.drinks)
                setListLength(response.data.drinks.length)
            })
        } else {
            setCocktailList([])
            setListLength(0)
        }
    }

    const drinkListRendered = () => {
        // console.log("drink list rendered")
        const drinks = cocktailList.map((d) => (
            <div
                style={{
                    width: "80vw",
                    backgroundColor: "green",
                    textAlign: 'left'
                }} 
                onClick={() => {
                    console.log(cocktailList.indexOf(d))
                    setShowDrink(true)
                    setCD(cocktailList.indexOf(d))
                }}
            >
                <img style={{height: '100px', width: '100px'}} src={d.strDrinkThumb}/>
                {cocktailList.indexOf(d)}.&nbsp; 
                {d.strDrink}
            </div>    
        ))
        return drinks
    }

    const drinkModal = () => {
        if (!showDrink) {
            return null
        } else {
            return (
                <div
                    style={{
                        height: "100%",
                        width: "100%",
                        zIndex: "101",
                        left: "0",
                        top: "0",
                        overflow: "auto",
                        position: "fixed",
                        display: "grid",
                        backgroundColor: "rgba(24, 25, 26, 0.8)",
                    }}

                    onClick={() => {
                        setCD(-1)
                        setShowDrink(false)
                    }}
                >
                    <div
                        style={{
                            position: "relative",
                            justifySelf: "center",
                            alignSelf: "center",
                            display: "block",
                            // border: "#ff6505 solid",
                            backgroundColor: "#FEF7E0",
                            height: "auto",
                            width: "auto",
                            zIndex: "102",
                            padding: "10px 0px 10px 0px",
                            borderRadius: "20px",
                        }}
                    >
                        {cocktailList[currentDrink].strDrink}
                    </div>
                </div>
            )
        }
    }

    return(
        <div>
            {drinkModal()}
            <div className="MainContent">
                You're early
                <br/>
                <input 
                    type="text"
                    onChange={(e) => {
                        console.log(e.target.value)
                        changeSearch(e.target.value)
                    }}
                    onKeyPress={(e) => {
                        if(e.key === 'Enter'){
                            console.log('enter press here! ')
                            runSearch();
                          }
                    }}
                />
                <button
                    onClick={() => {
                        runSearch()
                    }}
                >
                    Test
                </button>
                <br/>
                <button
                    onClick={() => {
                        console.log(cocktailList)
                    }}
                >
                    Test List
                </button>
                <br/>
                <div>
                    {listLength} results
                </div>
                <br/>
                <div>
                    {drinkListRendered()}
                </div>
            </div>
        </div>
    )

}

export default Cocktails
