import { useEffect, useState } from "react";
import "./dropdown_box.css"

function DropdownBox(props) {
    const[toggleVal, setToggle] = useState(false);

    var items = props.items;
    var columns = props.columns;
    var color = props.color;
    var title = props.title;
    // var items = ["Data Structures & Algorithms in Python", "Math Foundations for Decisions & Data Sciences", "AI and Data Engineering", "Data Mining"]
    // var columns = 2;

    var itemWidth = (1/columns)*100;
    console.log(itemWidth)


    return (
        <div onClick={() => {setToggle(!toggleVal)}} style={{width: "100%", border: "10px"}}>
            {
                (!toggleVal) ? (
                    <div className="info" style={{backgroundColor: `${color}`, color: "white"}}>
                        <span className="triangleright"/>
                        {title}
                    </div>
                ) : (
                    <div>
                        <div
                            className="info" 
                            style={{backgroundColor: `${color}`, color: "white"}}>
                            <span className="triangledown"/>
                            {title}
                        </div>
                        <div style={{width: "100%", backgroundColor: "transparent"}}>
                            {
                                items.map((item) => (
                                    <div 
                                        className="info"
                                        style={{
                                            width: `${itemWidth}%`, 
                                            float: "left", 
                                            textAlign: "center",
                                        }}>
                                        {item}
                                    </div>
                                ))
                            }
                        </div>


                    </div>
                )
            }
        </div>
    )
}

export default DropdownBox;
