import { useEffect, useState } from "react";
import "./dropdown_box.css"

function DropdownBox(props) {
    const[toggleVal, setToggle] = useState(false);

    // var items = props.items;
    var items = ["CMPE 255 - Data Mining", "CMPE 252 - Artificial Intelligence", "c"]
    var columns = 3;

    var itemWidth = (1/columns)*100;


    return (
        <div onClick={() => {setToggle(!toggleVal)}} style={{width: "100%"}}>
            {
                (toggleVal) ? (
                    <div style={{backgroundColor: "red"}}><span className="triangleright"/>Relevant Coursework</div>
                ) : (
                    <div>
                        <div style={{backgroundColor: "red"}}><span className="triangledown"/>
                        Relevant Coursework</div>
                        {
                            items.map((item) => (
                                <div style={{width: `${itemWidth}%`}}>{item}</div>
                            ))
                        }
                    </div>
                )
            }
        </div>
    )
}

export default DropdownBox;
