import {useEffect, useState} from "react";
import ReactSlider from "react-slider";
import axios from "axios";

function Cocktails() {
    const[searchTerm, changeSearch] = useState("");
    const[cocktailList, setCocktailList] = useState([]);
    const[listLength, setListLength] = useState(0);
    const[showDrink, setShowDrink] = useState(false);

    const drinkListRendered = () => {
        // console.log("drink list rendered")
        const drinks = cocktailList.map((d) => (
            <div 
                onClick={() => {
                    console.log(cocktailList.indexOf(d))
                }}
            >
                {/* <img src={d.strDrinkThumb}/> */}
                {cocktailList.indexOf(d)}.&nbsp; 
                {d.strDrink}
            </div>    
        ))
        return drinks
    }

    return(
        <div>
            <div className="MainContent">
                You're early
                <br/>
                <input 
                    type="text"
                    onChange={(e) => {
                        console.log(e.target.value)
                        changeSearch(e.target.value)
                    }}
                />
                <button
                    onClick={() => {
                        if (searchTerm != "") {
                            axios.get("https://www.thecocktaildb.com/api/json/v1/1/search.php?s="+searchTerm)
                            .then((response) => {
                                setCocktailList(response.data.drinks)
                                setListLength(response.data.drinks.length)
                            })
                        } else {
                            setCocktailList([])
                            setListLength(0)
                        }
                    }}
                >
                    Test
                </button>
                <br/>
                <button
                    onClick={() => {
                        console.log(cocktailList)
                    }}
                >
                    Test List
                </button>
                <br/>
                <div>
                    {listLength} results
                </div>
                <br/>
                <div>
                    {drinkListRendered()}
                </div>
            </div>
        </div>
    )

}

export default Cocktails
