import "./SkillBar.css"

function SkillBar(props) {
    // if (props.name == null || props.percent == null) {
    //     return null
    // }
    var percent = props.percent;
    var color = props.color;
    var ability = '';

    if (props.percent == null) {
        percent = Math.random() * 100
    }

    if (props.color == null) {
        color= `rgb(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255})`
    }

    if (props.enableAbility == true) {
        if (percent < 20) {
            ability = "Unaware"
        }
        if (percent >= 20) {
            ability = "Aware"
        }
        if (percent >= 40) {
            ability = "Basic Understanding"
        }
        if (percent >= 50) {
            ability = "Some Experience"
        }
        if (percent >= 70) {
            ability = "Experienced"
        }
        if (percent >= 90) {
            ability = "Proficient"
        }
    }

    return(
        <div style={{width: '100%'}}>

            <h1 style={{marginTop: '20px'}}>{props.name}</h1>
            <div style={{width: '100%', height: '42px'}}>
                <div style={{backgroundColor:`${color}`, width: `${percent}%`, height: '42px', float: 'left'}}/>
                <div style={{backgroundColor:'gray', width: `${100 - percent}%`, height: '42px', float: 'left'}}/>

            </div>
            {
                props.enableAbility
                    ? (
                        <div style={{width: '100%', height: '42px', border:'solid'}}>
                            {/* <div style={{width: `${percent}%`, height: '42px', float: 'left'}}/> */}
                            {/* <div className="abilityText" style={{float: 'left', textAlign: 'center', border: 'solid'}}>
                                {ability}
                            </div> */}
                            <div style={{transform: `translate(${percent})`}}>
                                {ability}
                            </div>
                            {/* <div style={{float: 'left',width: '100%'}}/> */}
                        </div>
                    )
                    : null
            }


        </div>
    )
}

export default SkillBar;
