import { useEffect, useState } from "react";
import "./dropdown_box.css"

function DropdownBox(props) {
    const[toggleVal, setToggle] = useState(false);

    var items = props.items;
    var columns = props.columns;
    var color = props.color;
    // var items = ["Data Structures & Algorithms in Python", "Math Foundations for Decisions & Data Sciences", "AI and Data Engineering", "Data Mining"]
    // var columns = 2;

    var itemWidth = (1/columns)*100;


    return (
        <div onClick={() => {setToggle(!toggleVal)}} style={{width: "100%"}}>
            {
                (!toggleVal) ? (
                    <div style={{backgroundColor: `${color}`}}><span className="triangleright"/><a>Relevant Coursework</a></div>
                ) : (
                    <div>
                        <div style={{backgroundColor: `${color}`}}><span className="triangledown"/>
                        <h1>Relevant Coursework</h1></div>
                        <div style={{display: "inline-block", width: "100%"}}>
                            {
                                items.map((item) => (
                                    <div style={{
                                        width: `${itemWidth}%`, 
                                        float: "left", 
                                        textAlign: "center",
                                    }}>
                                        {item}
                                    </div>
                                ))
                            }
                        </div>


                    </div>
                )
            }
        </div>
    )
}

export default DropdownBox;
