import { useEffect, useState } from "react";
import axios from "axios";

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

function Wordle() {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
    const [currentWord, setWord] = useState("");
    const [currentGuess, setGuess] = useState("");
    const [guessCount, setCount] = useState(0);
    const [guessHistory, setHistory] = useState([]);
    const [win, setWin] = useState(false);

    const [rightLetters, setRight] = useState([]);
    const [missedLetters, setMissed] = useState([]);
    const [wrongLetters, setWrong] = useState([]);

    const kbTop = ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P']
    const kbMid = ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L']
    const kbBot = ['Z', 'X', 'C', 'V', 'B', 'N', 'M']

    var wordList = ["tower", "water", "actor", "agent"]

    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const generateWord = () => {
        // for now get word from wordlist
        var rand = 0
        var word = wordList[rand]
        console.log(word)
        setWord(word)
    }

    const submitGuess = (guess) => {
        // console.log(guess.length)
        // if (!currentWord) {
        //     generateWord()
        // }
        if (!guess || guess.length != 5) {
            console.log("Guess must be 5 letters")
        } else {
            var ret = []
            var didWin = true;

            for (var i in guess) {
                console.log(guess[i], currentWord[i])
                var val = -1

                if (guess[i] == currentWord[i]) {
                    val = 1
                } else if (currentWord.indexOf(guess[i]) >= 0) {
                    val = 0
                }

                if (val != 1) {
                    didWin = false
                }

                if (val == 1) {
                    var tempRight = rightLetters
                    tempRight.push(guess[i])
                    setRight(tempRight)
                }

                if (val == 0) {
                    var tempMissed = missedLetters
                    tempMissed.push(guess[i])
                    setMissed(tempMissed)
                }

                if (val == -1) {
                    var tempWrong = wrongLetters
                    tempWrong.push(guess[i])
                    setWrong(tempWrong)
                }
                ret.push([guess[i], val])
            }
            console.log(ret)
            console.log(didWin)
            setWin(didWin)
            var tempHist = guessHistory
            tempHist.push(ret)
            setHistory(tempHist)


        }
        return ret
    }

    const renderGuesses = () => {
        if (guessHistory.length == 0) {
            return null
        }

        const guesses = guessHistory.map((g) => (
            <div>
                {renderGuess(g)}
            </div>
        ))

        return guesses
    }

    const renderGuess = (i) => {
        const c = i.map((c) => (
            (c[1] == 0) ? (
                <div className="wordleLetter" style={{backgroundColor: "#cdab4b", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                    {c[0].toUpperCase()}
                </div>
            ) : (
                (c[1] == 1) ? (
                    <div className="wordleLetter"  style={{backgroundColor: "green", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                        {c[0].toUpperCase()}
                    </div>
                ) : (
                    (c[1] == -1) ? (
                        <div className="wordleLetter"  style={{backgroundColor: "gray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {c[0].toUpperCase()}
                        </div>
                    ) : (<div>error</div>)
                )
            )   
        ))
        return c
    }

    const renderKeyBoard = () => {
        console.log("E in right", rightLetters.indexOf('E'.toLowerCase) != -1)
        const t = kbTop.map((k) => (
            (rightLetters.indexOf(k.toLowerCase) != -1) ? (
                <div className="wordleLetter"  style={{backgroundColor: "green", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                    {k}
                </div>
            ) : (
                (k.toLowerCase() in missedLetters) ? (
                    <div className="wordleLetter"  style={{backgroundColor: "#cdab4b", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                        {k}
                    </div>
                ) : (
                    (k.toLowerCase() in wrongLetters) ? (
                        <div className="wordleLetter"  style={{backgroundColor: "darkGray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    ) : (
                        <div className="wordleLetter"  style={{backgroundColor: "gray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    )
                )
            )
        ))

        const m = kbMid.map((k) => (
            (rightLetters.indexOf(k.toLowerCase) != -1) ? (
                <div className="wordleLetter"  style={{backgroundColor: "green", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                    {k}
                </div>
            ) : (
                (k.toLowerCase() in missedLetters) ? (
                    <div className="wordleLetter"  style={{backgroundColor: "#cdab4b", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                        {k}
                    </div>
                ) : (
                    (k.toLowerCase() in wrongLetters) ? (
                        <div className="wordleLetter"  style={{backgroundColor: "darkGray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    ) : (
                        <div className="wordleLetter"  style={{backgroundColor: "gray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    )
                )
            )
        ))

        const b = kbBot.map((k) => (
            (rightLetters.indexOf(k.toLowerCase) != -1) ? (
                <div className="wordleLetter"  style={{backgroundColor: "green", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                    {k}
                </div>
            ) : (
                (k.toLowerCase() in missedLetters) ? (
                    <div className="wordleLetter"  style={{backgroundColor: "#cdab4b", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                        {k}
                    </div>
                ) : (
                    (k.toLowerCase() in wrongLetters) ? (
                        <div className="wordleLetter"  style={{backgroundColor: "darkGray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    ) : (
                        <div className="wordleLetter"  style={{backgroundColor: "gray", display: "inline", padding: "1px", margin: "1px", fontWeight: "bold"}}>
                            {k}
                        </div>
                    )
                )
            )
        ))

        return (
            <div>
                <div>{t}</div>
                <div>{m}</div>
                <div>{b}</div>
            </div>
        )
    }

    return (
        <div>
            Wordle sim
            <br/>
            <button onClick={() => {
                generateWord()
                setCount(0)
                setHistory([])
                setWin(false)
            }}>
                New Game
            </button>
            <div>{guessCount}</div>
            <button onClick={() => {
                console.log(rightLetters)
                console.log(missedLetters)
                console.log(wrongLetters)
            }}>Test</button>
            <br/>
            <input
                // Need to clear input on button press 
                defaultValue={currentGuess}
                onChange={(e)=>{
                    console.log(e.target.value)
                    setGuess(e.target.value)
                }}
            />
            <button onClick={() => {
                if (guessCount <= 6 && win == false) {
                    submitGuess(currentGuess.toLowerCase())
                    setCount(guessCount + 1)
                }

            }}>
                Submit guess
            </button>
            <div>
                {renderGuesses()}
            </div>
            <br/>
            <div>
                {renderKeyBoard()}
            </div>
            {/* <div>
                Right Letters: {rightLetters}
            </div>
            <div>
                Misplaced Letters: {missedLetters}
            </div>
            <div>
                Wrong Letters : {wrongLetters}
            </div> */}
        </div>
    )
}

export default Wordle;
