import { useEffect, useState } from "react";
import axios from "axios";

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
        width,
        height
    };
}

function Wordle() {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
    const [currentWord, setWord] = useState("");
    const [currentGuess, setGuess] = useState("");
    const [guessCount, setCount] = useState(0);
    const [guessHistory, setHistory] = useState([]);
    const [win, setWin] = useState(false);

    var wordList = ["tower", "water", "actor", "agent"]

    useEffect(() => {
        function handleResize() {
            setWindowDimensions(getWindowDimensions());
        }

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const generateWord = () => {
        // for now get word from wordlist
        var rand = 0
        var word = wordList[rand]
        console.log(word)
        setWord(word)
    }

    const submitGuess = (guess) => {
        // console.log(guess.length)
        // if (!currentWord) {
        //     generateWord()
        // }
        if (!guess || guess.length != 5) {
            console.log("Guess must be 5 letters")
        } else {
            var ret = []
            var didWin = true;

            for (var i in guess) {
                console.log(guess[i], currentWord[i])
                var val = -1
                if (guess[i] == currentWord[i]) {
                    val = 1
                } else if (currentWord.indexOf(guess[i]) >= 0) {
                    val = 0
                }

                if (val != 1) {
                    didWin = false
                }
                ret.push([guess[i], val])
            }
            console.log(ret)
            console.log(didWin)
            setWin(didWin)
            var tempHist = guessHistory
            tempHist.push(ret)
            setHistory(tempHist)


        }
        return ret
    }

    const renderGuesses = () => {
        if (guessHistory.length == 0) {
            return null
        }

        const guesses = guessHistory.map((g) => (
            <div>
                {renderGuess(g)}
            </div>
        ))

        return guesses
    }

    const renderGuess = (i) => {
        const c = i.map((c) => (
            (c[1] == 0) ? (
                <div className="wordleLetter" style={{backgroundColor: "#cdab4b", display: "inline", padding: "1px", margin: "1px"}}>
                    {c[0]}
                </div>
            ) : (
                (c[1] == 1) ? (
                    <div className="wordleLetter"  style={{backgroundColor: "green", display: "inline", padding: "1px", margin: "1px"}}>
                        {c[0]}
                    </div>
                ) : (
                    (c[1] == -1) ? (
                        <div className="wordleLetter"  style={{backgroundColor: "gray", display: "inline", padding: "1px", margin: "1px"}}>
                            {c[0]}
                        </div>
                    ) : (<div>error</div>)
                )
            )



        ))
        return c
    }

    return (
        <div>
            Hello World
            <br/>
            <button onClick={() => {
                generateWord()
                setCount(0)
                setHistory([])
                setWin(false)
            }}>
                New Game
            </button>
            <div>{guessCount}</div>
            <button onClick={() => {
                console.log(guessHistory)
            }}>Test</button>
            <br/>
            <input onChange={(e)=>{
                console.log(e.target.value)
                setGuess(e.target.value)
            }}/>
            <button onClick={() => {
                if (guessCount <= 6 && win == false) {
                    submitGuess(currentGuess.toLowerCase())
                    setCount(guessCount + 1)
                }

            }}>
                Submit guess
            </button>
            <div>
                {renderGuesses()}
            </div>
        </div>
    )
}

export default Wordle;
